import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import Webcam from "react-webcam";

export default function Try() {
  const webcamRef = useRef(null);
  const [raw, setRaw] = useState("—");
  const [cleaned, setCleaned] = useState("—");
  const [error, setError] = useState(null);

  const resetBuffer = async () => {
    try {
      await axios.post("http://localhost:8000/reset");
      setRaw("—");
      setCleaned("—");
    } catch { setError("Reset failed"); }
  };

  useEffect(() => {
    const id = setInterval(async () => {
      try {
        const res = await axios.get("http://localhost:8000/buffer");
        setRaw(res.data.raw || "—");
        setCleaned(res.data.cleaned || "—");
        setError(null);
      } catch { setError("Backend offline"); }
    }, 1000);
    return () => clearInterval(id);
  }, []);

  return (
    <div className="min-h-screen bg-slate-50 flex flex-col items-center py-8 px-4">
      <div className="max-w-6xl w-full grid grid-cols-1 lg:grid-cols-2 gap-8">
        <section className="bg-white rounded-lg shadow border border-slate-200 overflow-hidden">
          <header className="px-4 py-2 border-b border-slate-100">
            <h2 className="text-lg font-medium text-slate-700">Live Camera</h2>
          </header>
          <Webcam
            ref={webcamRef}
            mirrored
            className="w-full h-auto object-cover bg-black"
          />
        </section>

        <section className="bg-white rounded-lg shadow border border-slate-200 flex flex-col justify-between">
          <div className="p-6 flex flex-col gap-6">
            <div>
              <h3 className="text-sm font-medium text-slate-500 mb-1">Raw Buffer</h3>
              <div className="min-h-[3rem] bg-slate-100 rounded-md border border-slate-300 px-3 py-2 font-mono text-slate-800">
                {raw}
              </div>
            </div>
            <div>
              <h3 className="text-sm font-medium text-slate-500 mb-1">Cleaned Sentence</h3>
              <div className="min-h-[3rem] bg-slate-50 rounded-md border border-emerald-400/60 px-3 py-2 font-semibold text-emerald-700">
                {cleaned}
              </div>
            </div>
            {error && <p className="text-sm text-red-600">{error}</p>}
          </div>
          <div className="py-4 px-6 border-t border-slate-100">
            <button
              onClick={resetBuffer}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 rounded-md shadow"
            >
              🔄 Reset
            </button>
          </div>
        </section>
      </div>
    </div>
  );
}
